sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === ''|| oldValue == newValue) {
		return;
	}
	var emailadd= new GlideAjax('GetEmailAddress');
	emailadd.addParam('sysparm_name','getEmail');
	emailadd.addParam('sysparm_userID',g_form.getValue('u_requested_for'));
	//Type appropriate comment here, and begin script below
	emailadd.getXML(populateEmailAdd);
	function populateEmailAdd(response) {
		var emailFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
		g_form.clearValue('u_requested_for_email');
		g_form.setValue('u_requested_for_email',emailFromScriptInclude);
	}
}
